CREATE TABLE usuarios (id_user INTEGER PRIMARY KEY AUTOINCREMENT, username TEXT, password TEXT, edad INTEGER, altura INTEGER, peso INTEGER);
CREATE TABLE ejercicio (id_ejercicio INTEGER PRIMARY KEY AUTOINCREMENT, ejercicio_name TEXT, tipo TEXT, instrucciones TEXT);
CREATE TABLE rutinas (id_rutina INTEGER PRIMARY KEY AUTOINCREMENT, rutina_name TEXT, pertenece TEXT);
CREATE TABLE lista_ejercicios (id_lista INTEGER PRIMARY KEY AUTOINCREMENT, ejercicio INTEGER, rutina INTEGER, calorias INTEGER, tiempo TEXT);